#include<iostream>
using namespace std;
char w[1024][26];int a[1024][4];int h[26],cnt,c[26],r[26],v[26],p[26][26],ans[1024],l,x,y,z,t,n;void I(int x,int y,int z){cnt++;a[cnt][0]=h[x];a[cnt][1]=y;a[cnt][2]=z;a[cnt][3]=0;h[x]=cnt;}int cmp(const void *a,const void *b){return strcmp((char*)b,(char*)a);}void D(int z){v[z]=1;for(int i=0;i<26;i++)if(!v[i]&&p[z][i])D(i);}int J(){int num=0,z=-1;for(int i=0;i<26;i++)if(c[i]-r[i]==1)z=i,num++;else if(r[i]-c[i]==1)num++;else if(r[i]!=c[i])return -1;if(num>2)return -1;if(z==-1)for(int i=0;i<26;i++)if(c[i]>0){z=i;break;}memset(v,0,sizeof(v));D(z);for(int i=0;i<26;i++)if(c[i]+r[i]&&!v[i])return -1;return z;}void E(int x,int y){a[y][3]=1;for(int i=h[x];i;i=a[i][0])if(!a[i][3])E(a[i][1],i);ans[l++]=a[y][2];}int main(){scanf("%d",&t);while(t--){scanf("%d",&n);for(int i=0;i<n;i++)scanf("%s",w[i]);qsort(w,n,sizeof(w[0]),cmp);memset(v,0,sizeof(v));memset(c,0,sizeof(c));memset(r,0,sizeof(r));memset(p,0,sizeof(p));memset(h,0,sizeof(h));cnt=0;for(int i=0;i<n;i++){x=w[i][0]-'a';y=w[i][strlen(w[i])-1]-'a';c[x]++;r[y]++;p[x][y]=1;I(x,y,i);}int z=J();if(z==-1)printf("***\n");else{l=0;E(z,0);for(int i=n-1;i>0;i--)printf("%s.",w[ans[i]]);printf("%s\n",w[ans[0]]);}}}
